import React from 'react';
import Document from "../document";
import reactCSS from 'reactcss';
import { SketchPicker } from 'react-color';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSortAmountUp,faSortAmountDown } from '@fortawesome/free-solid-svg-icons'
import CPSetting from "./CPSetting";

class ToolsPanel extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      colors: [
        {
          r: '241',
          g: '112',
          b: '19',
          a: '1',
        },
        {
          r: '241',
          g: '112',
          b: '19',
          a: '1',
        },
        {
          r: '241',
          g: '112',
          b: '19',
          a: '1',
        }
      ],
    };
  }

  //React Color Setting

  state = {
    displayColorPicker: false, // Display CP or not - false is not display
    color: {
      r: '241',
      g: '112',
      b: '19',
      a: '1',
    },
  };

  // when click, true
  handleClick = () => {
    this.setState({ displayColorPicker: !this.state.displayColorPicker })
  };

  // when click, false
  handleClose = () => {
    this.setState({ displayColorPicker: false })
  };


  handleChange = (color) => {
    this.setState({ color: color.rgb })
  };

  render() {

    const colors = Document[0].colors;
    const li = [];

    //React Color Setting -- Start

    const styles = reactCSS({
      'default': {
        color: {
          width: '25px',
          height: '25px',
          borderRadius: '2px',
          background: `rgba(${ this.state.color.r }, ${ this.state.color.g }, ${ this.state.color.b }, ${ this.state.color.a })`,
        },
        swatch: {
          padding: '3px',
          background: '#fff',
          borderRadius: '1px',
          boxShadow: '0 0 0 1px rgba(0,0,0,.1)',
          display: 'inline-block',
          cursor: 'pointer',
        },
        popover: {
          position: 'absolute',
          zIndex: '2',
          marginLeft: '30px',
          marginTop: '10px'
        },
        cover: {
          position: 'fixed',
          top: '0px',
          right: '0px',
          bottom: '0px',
          left: '0px',
        },
      },
    });

    //React Color Setting  -End

    for (const [index, value] of colors.entries()) {
      li.push(<li key={index}>
        <span style={{'background': value}}>
        </span>
        </li>);
    }

    return (
      <section className="section-toolspanel section-bottom">
        <ul>
          <li><FontAwesomeIcon icon={faSortAmountUp} /></li>
          <li><FontAwesomeIcon icon={faSortAmountDown} /></li>
        </ul>
        <ul>{li}</ul>


        <div style={ styles.swatch } onClick={ this.handleClick }>
          <div style={ styles.color } />
        </div>
        {/*
          Conditional operator = condition ? true : false
          */}
        {
          this.state.displayColorPicker ? <div style={ styles.popover }>
          <div style={ styles.cover } onClick={ this.handleClose }/>
          <SketchPicker color={ this.state.color } onChange={ this.handleChange } /></div> : null
        }
        <CPSetting />

      </section>
    );

  }
}


export default ToolsPanel;
