import React from 'react';
import Document from "../document";
import reactCSS from 'reactcss';
import { SketchPicker } from 'react-color';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSortAmountUp,faSortAmountDown } from '@fortawesome/free-solid-svg-icons'

class CPSetting extends React.Component {

  //React Color Setting

  state = {
    displayColorPicker: false,
    color: '#000000',
  };

  // when click, true
  handleClick = () => {
    this.setState({ displayColorPicker: !this.state.displayColorPicker })
  };

  // when click, false
  handleClose = () => {
    this.setState({ displayColorPicker: false })
  };


  handleChange = (color) => {
    this.setState({ color: color.hex })
  };

  render() {

    //React Color Setting -- Start

    const styles = reactCSS({
      'default': {
        color: {
          width: '25px',
          height: '25px',
          borderRadius: '2px',
          //background: `rgba(${ this.state.color.r }, ${ this.state.color.g }, ${ this.state.color.b }, ${ this.state.color.a })`,
          background: `#'${color.hex}`,
        },
        swatch: {
          padding: '3px',
          background: '#fff',
          borderRadius: '1px',
          boxShadow: '0 0 0 1px rgba(0,0,0,.1)',
          display: 'inline-block',
          cursor: 'pointer',
        },
        popover: {
          position: 'absolute',
          zIndex: '2',
          marginLeft: '30px',
          marginTop: '10px'
        },
        cover: {
          position: 'fixed',
          top: '0px',
          right: '0px',
          bottom: '0px',
          left: '0px',
        },
      },
    });

    //React Color Setting  -End

    return (
      <section className="section-toolspanel section-bottom">


        <div style={ styles.swatch } onClick={ this.handleClick }>
          <div style={ styles.color } />
        </div>
        {/*
          Conditional operator = condition ? true : false
          */}
        {
          this.state.displayColorPicker ? <div style={ styles.popover }>
          <div style={ styles.cover } onClick={ this.handleClose }/>
          <SketchPicker color={ this.state.color } onChange={ this.handleChange } /></div> : null
        }

      </section>
    );

  }
}


export default CPSetting;
