import React from 'react';
import DraggableItem from "./draggableItem";
import CPSetting from "./CPSetting";
import ManageData from "./manageData";

class Artboard extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      color : null
    };
    this.ChildRef = React.createRef();
  }

  sendHex(){
    console.log('Parent! - '+ this.refs.CPSetting.sendHex());
    this.setState({
      color : this.ChildRef.CPSetting.sendHex()
    });
  }



  render() {
    const style={
      "background": "#fff",
      "position": "absolute",
      "top": "50%",
      "left": "50%",
      "transform": "translate(-50%,-50%)"
      //"border": "solid 1px red"
    }

    const width=500;
    const height=400;

    return (
      <section style={{"position":"relative"}} className="section-artboard section-bottom">
      <button onClick={this.sendHex}></button><p>{this.state.color}</p>
      <CPSetting ref="this.ChildRef"/>
      <ManageData color="#000000"/>
      <DraggableItem />
      </section>
    );
  }
}

export default Artboard;
