import React from 'react';
import { Link } from 'react-router-dom'
import '../../styles/menubar.scss';
import icon from '../../images/logo.png';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChevronDown,faAdjust } from '@fortawesome/free-solid-svg-icons'
import ReactModal from 'react-modal';

class TopBar extends React.Component {

  // React Modal -- Start
  constructor () {
    super();
    this.state = {
      showModal: false
    };

    this.handleOpenModal = this.handleOpenModal.bind(this);
    this.handleCloseModal = this.handleCloseModal.bind(this);
  }

  handleOpenModal () {
    this.setState({ showModal: true });
  }

  handleCloseModal () {
    this.setState({ showModal: false });
  }
  // React Modal -- End

  render() {

    const styles = {
      overlay: {
        background: 'none',
      },
      content : {
        position: 'relative',
        width: '250px',
        top                   : '11.4%',
        left                  : '48%',
        right                 : 'auto',
        bottom                : 'auto',
        transform             : 'translate(-50%, -10%)',
        borderRadius: '0',
        border: 'solid 1px #F0F0F0',
        transition: 'ease all 1s',
      }
    };

    return (
      <section className="section-menubar">
        <div>
        <Link to="/home" aria-label="Home" title="Home"><img className="icon" src={icon} alt="Icon" /></Link>
        <span>Export</span>
        </div>

        <div className="export-pannel">
          <div className="export-pannel-content">
            <div>
              <h2>Preview</h2>
              <svg
                  id="svg"
                  version="1.1"
                  width="100%"
                  height="auto"
                  viewBox="0 0 600 400"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="#000"
                  dangerouslySetInnerHTML={{__html: this.props.data.join('') }}
              >
              </svg>
              <div>
                <table>
                <tr>
                  <th>File Name</th>
                  <td>Document Name</td>
                </tr>
                <tr>
                  <th>Scale</th>
                  <td>600px : 300px</td>
                </tr>
                <tr>
                  <th>Size</th>
                  <td>It'll be ... 100KB</td>
                </tr>
                </table>
              </div>
            </div>
            <div>
              <h2>File Name</h2>
              <input type="text" value="Document Name"/>
              <h2>Format</h2>

              <ul>
                <li><label><input type="radio" name="format" value="png" checked />PNG</label></li>
                <li><label><input type="radio" name="format" value="jpg" />JPG</label></li>
                <li><label><input type="radio" name="format" value="svg" />SVG</label></li>
              </ul>

              <h2>Size</h2>
              <ul>
                <li><label><input type="radio" name="size" value="small" checked />Small</label></li>
                <li><label><input type="radio" name="size" value="medium" />Medium</label></li>
                <li><label><input type="radio" name="size" value="large" />Large</label></li>
              </ul>
              <h2>Compression ratio</h2>
              <bottom>Cancel</bottom><bottom>Download</bottom>
            </div>
          </div>
        </div>

        <div>
        <button className="modal-botton" onClick={this.handleOpenModal}>
          <p>Document name <span><FontAwesomeIcon icon={faChevronDown} /></span></p>
        </button>
        <ReactModal style={styles} isOpen={this.state.showModal} contentLabel="Change Document Information">
          <form className="form-document" action="">
          <p>Change a document name.</p>
          <input type="text" value="Document name"/>
          <button onClick={this.handleCloseModal}>Cancel</button>
          <input type="submit" value="Submit"/>
          </form>
        </ReactModal>
      </div>
        <div className="mode-change"><FontAwesomeIcon icon={faAdjust} /></div>
      </section>
    );

}
}

export default TopBar;
