import React from 'react';
import Document from "../document";
import reactCSS from 'reactcss';
import { SketchPicker } from 'react-color';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSortAmountUp,faSortAmountDown } from '@fortawesome/free-solid-svg-icons'
import CPSetting from "./CPSetting";

class ToolsPanel extends React.Component {

  getColor() {
    this.setState({
      color: this.refs.CPSetting.sendCount()
    });
  }

  render() {


    return (
      <section className="section-toolspanel section-bottom">
        {this.state.count}
        <ul>
          <li><FontAwesomeIcon icon={faSortAmountUp} /></li>
          <li><FontAwesomeIcon icon={faSortAmountDown} /></li>
        </ul>

        <div className="color-scheme">
          <CPSetting color="#B21313"/>
          <CPSetting color="#111184"/>
          <CPSetting color="#C7B136"/>
        </div>

      </section>
    );

  }
}


export default ToolsPanel;
