import React from 'react';
import { Redirect } from 'react-router-dom'

//import {
//  BrowserRouter as Router,
//  Switch,
//  Route,
//  Link
//} from "react-router-dom";

import Data from "../data";
import ColorPicker from '../components/ColorPicker.js';

class ColorSchemes extends React.Component {

  // Start - Get API Json

  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      users: [],
    };
  }

  componentDidMount() {
    fetch("/api-of-user-json")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            users: result.users,
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  handleChange = e => this.setState({ [e.target.name]: e.target.value });

  // END - Get API Json

  render() {
    const { error, isLoaded, users } = this.state;
    if (error) {
      return <section className="section-home">Error: {error.message}<Redirect to="/login" /></section>;

    } else if (!isLoaded) {
      return <section className="section-home">Loading...</section>;
    } else {

      const json = users.color_schemes;
      console.log(json);
      return (
        <div className="section-color-scheme">
          <div className="create-color-scheme">
            <h2>Create a new color scheme</h2>
            <form action="/createcolorschemes" method="post">
              <div>
                <input type="text" name="name" placeholder="Color Scheme Name"/>
                <br/>
                <ColorPicker onChange={this.handleChange}/>
                <ul>
                  <li><input type="color" name="main"/></li>
                  <li><input type="color" name="sub"/></li>
                  <li><input type="color" name="accent"/></li>
                </ul>
              </div>
              <input type="submit" value="Create"/>
            </form>
          </div>

          <div className="color-scheme-list">
            <div>
              {users.map(item => (
                <div className="color-scheme-item">
                <ul className="color-scheme">
                {JSON.parse(item.color_schemes).map((c,key) => (
                  <li>
                    <p>{c.name}</p>
                    <span style={{'background': c.main}}></span>
                    <span style={{'background': c.sub}}></span>
                    <span style={{'background': c.accent}}></span>
                    <form action="/deletecolorscheme" method="post">
                      <button type="submit" name="name" value={key}>Delete</button>
                    </form>
                  </li>
                ))}
                </ul>
                </div>
              ))}
            </div>
          </div>
        </div>
      );
    }
  }
}

export default ColorSchemes;
