import React from 'react';
import DraggableItem from "./draggableItem";
import CPSetting from "./CPSetting";

class Artboard extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      color : null
    };
  }
  componentDidUpdate() {
    this.setState({
      color : this.refs.CPSetting.sendHex()
    });
  }
  getCount(){
    this.setState({
      color : this.refs.CPSetting.sendHex()
    });
  }

  render() {
    const style={
      "background": "#fff",
      "position": "absolute",
      "top": "50%",
      "left": "50%",
      "transform": "translate(-50%,-50%)"
      //"border": "solid 1px red"
    }

    const width=500;
    const height=400;

    return (
      <section style={{"position":"relative"}} className="section-artboard section-bottom">
      <p>Color is {this.state.color}</p>
      <DraggableItem />
      </section>
    );
  }
}

export default Artboard;
