import React from 'react';
import '../../styles/atelier.scss';

//import { onWheel } from './features/pinch-gesture-wheel'

//====================================
//  We need below functions that
//  -[△] drag items
//  -[x] duplicate items
//  -[x] change items' color
//  -[ ] resize items
//  -[ ] reflect items
//  -[x] delete items
//  -[ ] group items
//  -[x] pinch-in & pinch-out
//====================================
// maybe helpful! -> https://github.com/daybrush/moveable
// https://medium.com/@auchenberg/detecting-multi-touch-trackpad-gestures-in-javascript-a2505babb10e

class Artboard extends React.Component {

  //====================================
  //  To drag elements of SVG
  //====================================
  //
  //  1. Calculate gap of (1) and (2)
  //     (1) Get a mouse coordinate when clicked
  //     initial
  //     (2) Get a mouse coodinate when moved
  //     moved
  //  2. Get element's translate(x,y) by Regax
  //  3. Add a gap to element translate(x,y) by Regax
  //
  //  draft...
  //  * make it clear which el of data should be edited
  //
  //====================================

  //--- Choose which el we should edit ---//

  constructor(props) {
    super(props);
    this.state = {
      isMouseDown : false,
      initialTranslate: [0,0], // to change translate(x,y)
      initial: [0,0], // to calculate a gap
      selectedElement: 0, // to make it clear which el should edit
      displayContextMenu: false,
      // -- below is artboard resize and pinch
      mouse: [0,0],
      artboardPosition: [0,0],
      artboardScale: 1,
      gestureStartScale: 0,
      startCoordinate: [0,0],
      // -- below is svg data
      data : [
      '<g transform="translate(50,50) scale(1)" class="sub" style="cursor:move"><circle cx="0" cy="0" r="50"></circle></g>',
      '<g transform="translate(100,250) scale(2)" class="main" style="cursor:move" border="solid 3px #000000"><circle cx="30" cy="30" r="20"></circle></g>',
      '<g transform="translate(50,150) scale(1)" class="accent" style="cursor:move"><circle cx="10" cy="10" r="15"></circle><circle cx="20" cy="20" r="10"></circle></g>',
      '<g transform="translate(50,100) scale(1)" style="cursor:move"><path class="main" d="M168.68,59.078l-70.627,40.776l-0,81.553l70.627,-40.776l-0,-81.553Z"></path><path d="M98.043,18.295l-70.627,40.777l70.637,40.782l70.627,-40.777l-70.637,-40.782Z" class="sub"></path><path d="M98.053,99.854l-70.66,-40.795l0,81.548l70.66,40.796l-0,-81.549Z" class="accent"></path></g>',
      '<g transform="translate(50,50) scale(1)" style="cursor:move"><path class="main" d="M168.68,59.078l-70.627,40.776l-0,81.553l70.627,-40.776l-0,-81.553Z"/><clipPath id="_clip1"><path d="M168.68,59.078l-70.627,40.776l-0,81.553l70.627,-40.776l-0,-81.553Z"/></clipPath><g clip-path="url(#_clip1)"><use xlink:href="#_Image2" x="99.571" y="59.401" width="69.632px" height="122.329px" transform="matrix(0.994742,-1.10439e-16,-6.12938e-33,0.994549,3.55271e-15,-3.55271e-15)"/></g><path d="M98.043,18.295l-70.627,40.777l70.637,40.782l70.627,-40.777l-70.637,-40.782Z" class="sub"/><clipPath id="_clip3"><path d="M98.043,18.295l-70.627,40.777l70.637,40.782l70.627,-40.777l-70.637,-40.782Z"/></clipPath><g clip-path="url(#_clip3)"><use xlink:href="#_Image4" x="27.559" y="18.394" width="141.263px" height="81.559px" transform="matrix(0.994813,0,-1.10425e-16,0.994616,1.42109e-14,0)"/></g><path d="M98.053,99.854l-70.66,-40.795l0,81.548l70.66,40.796l-0,-81.549Z" class="accent"/><clipPath id="_clip5"><path d="M98.054,99.857l-70.66,-40.795l0,81.548l70.66,40.796l0,-81.549Z"/></clipPath><g clip-path="url(#_clip5)"><use xlink:href="#_Image6" x="28.526" y="59.379" width="69.665px" height="122.344px" transform="matrix(0.995207,1.1049e-16,0,0.994666,0,0)"/></g><defs><image id="_Image2" width="70px" height="123px" xlink:href="data:image/png;base64,"/><image id="_Image4" width="142px" height="82px" xlink:href="data:image/png;base64,"/><image id="_Image6" width="70px" height="123px" xlink:href="data:image/png;base64,"/></defs></g>'
    ],
    };
