import React from 'react';

class Artboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }
  componentDidMount() {
    fetch("/api/v1/list")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result.items
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <section className="section-artboard section-bottom">Error: {error.message}</section>;
    } else if (!isLoaded) {
      return <section className="section-artboard section-bottom">Loading...</section>;
    } else {
      const style={
        "background": "#fff",
        "position": "absolute",
        "top": "50%",
        "left": "50%",
        "transform": "translate(-50%,-50%)"
        //"border": "solid 1px red"
      }
      return (
        <section style={{"position":"relative"}} className="section-artboard section-bottom">
        <svg style={style} width="500" height="500">
        <circle cx="50" cy="50" r="40" stroke="green" stroke-width="4" fill="yellow" />
        </svg>
        </section>
      );
    }
  }
}

export default Artboard;
