import React from 'react';
//import DraggableItem from "./draggableItem";

//====================================
//  We need below functions that
//  -[ ] drag items
//  -[ ] duplicate items
//  -[x] change items' color
//  -[ ] resize items
//  -[ ] reflect items
//  -[ ] delete items
//  -[ ] group items
//====================================

class Artboard extends React.Component {

  //====================================
  //  To drag elements of SVG
  //====================================
  //
  //  1. Calculate gap of (1) and (2)
  //     (1) Get a mouse coordinate when clicked
  //     initial
  //     (2) Get a mouse coodinate when moved
  //     moved
  //  2. Get element's translate(x,y) by Regax
  //  3. Add a gap to element translate(x,y) by Regax
  //
  //  draft...
  //  * make it clear which el of data should be edited
  //
  //====================================

  //--- Choose which el we should edit ---//

  constructor(props) {
    super(props);
    this.state = {
      isMouseDown : false,
      initialTranslate: [0,0], // to change translate(x,y)
      initial: [0,0], // to calculate a gap
      selectedElement: 0, // to make it clear which el should edit
      data : [
      '<g transform="translate(50,50) scale(1)" class="sub" fill="#ccc"><circle cx="0" cy="0" r="50"></circle></g>',
      '<g transform="translate(100,250) scale(2)" class="main" fill="#ccc"><circle cx="30" cy="30" r="20"></circle></g>',
      '<g transform="translate(50,150) scale(1)" class="accent" fill="#ccc"><circle cx="10" cy="10" r="15"></circle><circle cx="20" cy="20" r="10"></circle></g>',
      '<g transform="translate(50,100)" fill="#ccc"><path class="main" d="M168.68,59.078l-70.627,40.776l-0,81.553l70.627,-40.776l-0,-81.553Z"/><path d="M98.043,18.295l-70.627,40.777l70.637,40.782l70.627,-40.777l-70.637,-40.782Z" class="sub"/><path d="M98.053,99.854l-70.66,-40.795l0,81.548l70.66,40.796l-0,-81.549Z" class="accent"/></g>'
    ],
    };
  }

  //--- Choose which element we should edit ---//
  selectElement = (e) => {
    var array = this.state.data;
    const g = e.target.parentNode.outerHTML;

    console.log(array[3]);
    console.log(g);

    this.setState(
      {
        selectedElement: array.indexOf(g)
      }
    );
  }

  //--- get initial translate of selected elements ---//
  getTranslate = (e) => {
    const g = e.target.parentNode.outerHTML;

    const regExp = /\d+/g;
    const translate = g.match(regExp)

    this.setState(
      {
        initialTranslate:[parseInt(translate[0]),parseInt(translate[1])]
      }
    );
  }

  onMouseDown = (e) => {
    this.setState({isMouseDown:true})

    const mouseX = e.pageX;// pageX and pageY is mouse's axis in the box.
    const mouseY = e.pageY;
    this.selectElement(e);

    //  Set this.state of
    //  * initialTranslate
    //  * mouse clicked coordinate
    this.getTranslate(e);
    this.setState({initial:[mouseX,mouseY]});
  }

  onMouseMove = (e) => {
    if (this.state.isMouseDown) {
      console.log(this.state.selectedElement);

      //---  Calculate a gap  ---//
      const move = [e.pageX,e.pageY];
      const gap = [move[0] - this.state.initial[0], move[1] - this.state.initial[1]];

      console.log('gap ok : ' +  gap);


      //---  Calculate a translate(x,y)  ---//
      let translate = [
        this.state.initialTranslate[0] + gap[0],
        this.state.initialTranslate[1] + gap[1]
      ];

      console.log('translate ok ' + translate);

      const g = e.target.parentNode.outerHTML;

      //console.log('initialTranslate('+this.state.initialTranslate[0]+','+this.state.initialTranslate[1]+')');

      //---  let a string have a proper translate(x,y)  ---//
      const regExp = /\d+/g;
      var n = 1;

      const result = g.replace(regExp,
        function(match) {
          if(n === 1) {
            n--;
            return translate[0];
          } else if (n === 0) {
            n--;
            return translate[1];
          } else {
            return match;
          };
        }
      );

      console.log('g tag is ' + result)

      //---  Update this.state.data  ---//

      const data_copy = this.state.data.slice();
      data_copy[this.state.selectedElement] = result;
      this.setState({data: data_copy});

      console.log(this.state.data[this.state.selectedElement]);


    }
  }

  onMouseUp = (e) => {
    this.setState({isMouseDown:false})
    console.log('mouseUp: ' + e.target.parentNode.outerHTML)
  }

  onMouseLeave = (e) => {
    if(this.state.isMouseDown) {
      this.setState({isMouseDown:false})
      console.log('mouseLeave: ' + e.target.outerHTML)
    }
  }


  render() {

    const style = {
      background: "#fff",
      position: "absolute",
      top: "50%",
      left: "50%",
      transform: "translate(-50%,-50%)",
    }

    return (
      <section style={{"position":"relative"}} className="section-artboard section-bottom">
      <svg
          style={style}
          id="svg"
          version="1.1"
          width="500"
          height="400"
          xmlns="http://www.w3.org/2000/svg"
          fill="#fff"
          dangerouslySetInnerHTML={{__html: this.state.data.join('') }}
          onMouseDown={this.onMouseDown}
          onMouseUp={this.onMouseUp}
          onMouseMove={this.onMouseMove}
          onMouseLeave={this.onMouseLeave}
      >
      </svg>
      {/*<DraggableItem
          mainColor={this.props.mainColor}
          subColor={this.props.subColor}
          accentColor={this.props.accentColor}
      />*/}
      </section>
    );
  }
}

export default Artboard;
