import React from 'react';
import DraggableItem from "./draggableItem";

//====================================
//  We need below functions that
//  -[ ] drag items
//  -[ ] duplicate items
//  -[x] change items' color
//  -[ ] resize items
//  -[ ] reflect items
//  -[ ] delete items
//  -[ ] group items
//====================================

class Artboard extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      data : [
      '<g transform="translate(50,350) scale(0.5)"><circle cx="150" cy="200" r="100" fill="#06f"></circle></g>',
      '<g transform="translate(100,250) scale(2)"><circle cx="30" cy="30" r="20" className="main"></circle></g>',
      '<g transform="translate(50,150) scale(1)"><circle cx="10" cy="10" r="15" fill="#000"></circle><circle cx="20" cy="20" r="10" fill="#ccc"></circle></g>',
      '<g transform="translate(50,100)"><path className="main" d="M168.68,59.078l-70.627,40.776l-0,81.553l70.627,-40.776l-0,-81.553Z"/><path d="M98.043,18.295l-70.627,40.777l70.637,40.782l70.627,-40.777l-70.637,-40.782Z" className="sub"/><path d="M98.053,99.854l-70.66,-40.795l0,81.548l70.66,40.796l-0,-81.549Z" className="accent"/></g>'
    ]
    };
  }


  render() {

    const style = {
      background: "#fff",
      position: "absolute",
      top: "50%",
      left: "50%",
      transform: "translate(-50%,-50%)",
    }

    return (
      <section style={{"position":"relative"}} className="section-artboard section-bottom">
      <svg
          style={style}
          id="svg"
          version="1.1"
          width="500"
          height="400"
          xmlns="http://www.w3.org/2000/svg"
          fill="#fff"
          dangerouslySetInnerHTML={{__html: this.state.data.join('') }}
      >
      </svg>
      {/*<DraggableItem
          mainColor={this.props.mainColor}
          subColor={this.props.subColor}
          accentColor={this.props.accentColor}
      />*/}
      </section>
    );
  }
}

export default Artboard;
