import React from 'react';

import MenuBar from '../components/menubar.js';
import GallaryPanel from '../components/atelier_gallarypanel.js';
import ToolsPanel from '../components/atelier_toolspanel.js';
import Artboard from '../components/atelier_artboard.js';

import '../styles/atelier.scss';

//====================================
//  We manage data such as
//  * mainColor's Hex
//  * subCoolor's Hex
//  * accentColor's Hex
//  in this file.
//  Each SVG items' colors are effected
//  from style jsx in this file.
//====================================

class Atelier extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      mainColor: "#B21313",
      subColor: "#111184",
      accentColor: "#C7B136",
    }
  }

  render() {

    return (
      <section className="section-atelier">
      <style jsx>{`
        .main {
          fill: ${this.state.mainColor};
        }
        .sub {
          fill: ${this.state.subColor};
        }
        .accent {
          fill: ${this.state.accentColor};
        }
      `}</style>
        <MenuBar />
        <ToolsPanel
             mainColor={this.state.mainColor}
             subColor={this.state.subColor}
             accentColor={this.state.accentColor}
             method={(e) => this.setState({accentColor:e})}
        />
        <Artboard
             mainColor={this.state.mainColor}
             subColor={this.state.subColor}
             accentColor={this.state.accentColor}
        />
        <GallaryPanel />
      </section>
    );

  }
}

export default Atelier;
