import React from 'react';

class Artboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }
  componentDidMount() {
    fetch("/api/v1/list")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result.items
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <section className="section-artboard section-bottom">Error: {error.message}</section>;
    } else if (!isLoaded) {
      return <section className="section-artboard section-bottom">Loading...</section>;
    } else {
      return (
        <section className="section-artboard section-bottom">
        <ul>
          {items.map(item => (
            <li key={item.name}>
              {item.name} {item.price}
            </li>
          ))}
        </ul>
        <svg width="100" height="100"></svg>
        </section>
      );
    }
  }
}

export default Artboard;
