/** 選んだ画像をドラッグして移動 **/

class DraggableItem extends React.Component {

  constructor(props) {
    super(props);
    this.state = {cx:100,cy:100};
  }

  componentDidMount() {
    this.timerID = setInterval(
      () => this.tick(),
      1000
    );
  }

  render() {
    return (
      <circle id="ball1"
              cx={this.state.cx}
              cy={this.state.cy}
              onMouseDown={onMouseDown}
              onMouseUp={onMouseUp}
              onMouseMove={onMouseMove}
              r="20" fill="blue" />
    )
  }
}

export default DraggableItem;
