import React from 'react';

//import {
//  BrowserRouter as Router,
//  Switch,
//  Route,
//  Link
//} from "react-router-dom";

import MenuBar from '../components/menubar.js';
import GallaryPanel from '../components/gallarypanel.js';
import ToolsPanel from '../components/toolspanel.js';
import Artboard from '../components/artboard.js';

import '../styles/atelier.scss';

class Atelier extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      mainColor: "#B21313",
      subColor: "#111184",
      accentColor: "#C7B136",
    }
  }

  render() {

    return (
      <section className="section-atelier">
      <style jsx>{`
        .main {
          color: green;
        }
        .sub {
          color: green;
        }
        .accent {
          color: ${this.state.accentColor};
        }
      `}</style>
        <MenuBar />
        <ToolsPanel
             mainColor={this.state.mainColor}
             subColor={this.state.subColor}
             accentColor={this.state.accentColor}
             method={(e) => this.setState({accentColor:e})}
        />
        <Artboard
             mainColor={this.state.mainColor}
             subColor={this.state.subColor}
             accentColor={this.state.accentColor}
        />
        <GallaryPanel />
      </section>
    );

  }
}

export default Atelier;
