import React, {useState, useRef} from "react"

const DraggableItem = () => {
    const [x, setX] = useState(50);
    const [y, setY] = useState(50);
    const [gap, setGap] = useState({x: 0, y: 0})
    const [isMouseDown, setIsMouseDown] = useState(false)
    const svgRef = useRef(null)
    const isSelect = useState(false)

    // マウス押下時
    const onMouseDown = (e) => {
        setIsMouseDown(true);
        const rect = e.currentTarget.getBoundingClientRect();
        const mouseX = e.pageX;
        const mouseY = e.pageY;
        setGap({ x: mouseX - rect.left, y: mouseY - rect.top });
    }

    // マウス押下解除時
    const onMouseUp = (e) => {
        setIsMouseDown(false);
    }

    const onMouseLeave = (e) => {
        setIsMouseDown(false);
    }

    // マウス移動時
    const onMouseMove = (e) => {
        // マウス押下中
        if (isMouseDown) {
            const mouseX = e.pageX;
            const mouseY = e.pageY;

            const svgRect = svgRef.current.getBoundingClientRect();
            const relativeX = mouseX - svgRect.left;
            const relativeY = mouseY - svgRect.top;

            setX(relativeX - gap.x);
            setY(relativeY - gap.y);
        }
    }

    const onSelect = (e, item) => {
      item = e.target;
        console.log(e.target)
    }

    const style = {
      background: "#fff",
      position: "absolute",
      top: "50%",
      left: "50%",
      transform: "translate(-50%,-50%)",
    }

    return (
   <svg
      style={style}
      id="svg"
      version="1.1"
      width="500"
      height="400"
      xmlns="http://www.w3.org/2000/svg"
      ref={svgRef}
      fill="#fff"
    >
      <rect
        onClick={onSelect}
        width="100"
        height="100"
        x={x}
        y={y}
        fill="#900"
        stroke="#666"
        strokeWidth="5"
        onMouseDown={onMouseDown}
        onMouseUp={onMouseUp}
        onMouseMove={onMouseMove}
        onMouseLeave={onMouseLeave}
      />
    </svg>
    )
}

function duplicateItems() {

}

export default DraggableItem;
