import React from 'react';
import DraggableItem from "./draggableItem";

//====================================
//  We need below functions that
//  -[ ] drag items
//  -[ ] duplicate items
//  -[x] change items' color
//  -[ ] resize items
//  -[ ] reflect items
//  -[ ] delete items
//  -[ ] group items
//====================================

class Artboard extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      data : [
      '<g transform="translate(50,350) scale(0.5)"><circle cx="150" cy="200" r="100" fill="#06f"></circle></g>',
      '<g transform="translate(100,250) scale(2)"><circle cx="30" cy="30" r="20" fill="skyblue"></circle></g>',
      '<g transform="translate(50,150) scale(1)"><circle cx="10" cy="10" r="15" fill="#fff"></circle><circle cx="20" cy="20" r="10" fill="#ccc"></circle></g>'
    ]
    };
  }


  render() {

    return (
      <section style={{"position":"relative"}} className="section-artboard section-bottom">
      {/*<DraggableItem
          mainColor={this.props.mainColor}
          subColor={this.props.subColor}
          accentColor={this.props.accentColor}
      />*/}
      </section>
    );
  }
}

export default Artboard;
