import React from 'react';
import { Link } from 'react-router-dom'
import '../styles/menubar.scss';
import icon from '../images/logo.png';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChevronDown } from '@fortawesome/free-solid-svg-icons'
import ReactModal from 'react-modal';

import Document from "../document";

class MenuBar extends React.Component {

  // React Modal -- Start
  constructor () {
    super();
    this.state = {
      showModal: false
    };

    this.handleOpenModal = this.handleOpenModal.bind(this);
    this.handleCloseModal = this.handleCloseModal.bind(this);
  }

  handleOpenModal () {
    this.setState({ showModal: true });
  }

  handleCloseModal () {
    this.setState({ showModal: false });
  }
  // React Modal -- End

  render() {

    const styles = {
      overlay: {
        background: 'none',
      },
      content : {
        position: 'relative',
        top                   : '10%',
        left                  : '50%',
        right                 : 'auto',
        bottom                : 'auto',
        transform             : 'translate(-50%, -10%)'
      }
    };

    return (
      <section className="section-menubar">
        <Link to="/home"><img className="icon" src={icon} alt="Icon" /></Link>
        <div>
        <button onClick={this.handleOpenModal}>
          <p>{Document[0].title} <span><FontAwesomeIcon icon={faChevronDown} /></span></p>
        </button>
        <ReactModal style={styles} isOpen={this.state.showModal} contentLabel="Change Document Information">
          <input type="text" placeholder="Document name"/>
          <button onClick={this.handleCloseModal}>Cancel</button>
        </ReactModal>
      </div>
        <div className="logout">Log out</div>
      </section>
    );

}
}

export default MenuBar;
