import React from 'react';
//import DraggableItem from "./draggableItem";

//====================================
//  We need below functions that
//  -[ ] drag items
//  -[ ] duplicate items
//  -[x] change items' color
//  -[ ] resize items
//  -[ ] reflect items
//  -[ ] delete items
//  -[ ] group items
//====================================

class Artboard extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      isMouseDown : false,
      translate: [0,0],
      data : [
      '<g transform="translate(50,50) scale(1)" class="sub"><circle cx="0" cy="0" r="50""></circle></g>',
      '<g transform="translate(100,250) scale(2)" class="main"><circle cx="30" cy="30" r="20"></circle></g>',
      '<g transform="translate(50,150) scale(1)" class="accent"><circle cx="10" cy="10" r="15"></circle><circle cx="20" cy="20" r="10"></circle></g>',
      '<g transform="translate(50,100)"><path class="main" d="M168.68,59.078l-70.627,40.776l-0,81.553l70.627,-40.776l-0,-81.553Z"/><path d="M98.043,18.295l-70.627,40.777l70.637,40.782l70.627,-40.777l-70.637,-40.782Z" class="sub"/><path d="M98.053,99.854l-70.66,-40.795l0,81.548l70.66,40.796l-0,-81.549Z" class="accent"/></g>'
    ],
    };
  }


  getTranslate = (e) => {
    const g = e.target.parentNode.outerHTML;

    const regExp = /\d+/g;
    const translate = g.match(regExp)

    console.log('getTranslate ' + g)
    console.log('translate('+g.match(regExp)[0]+','+translate[1]+')');
  }

  onMouseDown = (e) => {
    this.setState({isMouseDown:true})

    const mouseX = e.pageX;// pageX and pageY is mouse's axis in the box.
    const mouseY = e.pageY;

    this.getTranslate(e);
  }

  onMouseMove = (e) => {
    if (this.state.isMouseDown) {
      //moved = [e.pageX,e.pageY];
      //console.log('mouseDown ' +  e.target.outerHTML)
      console.log('gap ');
    }
  }

  onMouseUp = (e) => {
    this.setState({isMouseDown:false})
    console.log('mouseUp: ' + e.target.outerHTML)
  }

  onMouseLeave = (e) => {
    if(this.state.isMouseDown) {
      this.setState({isMouseDown:false})
      console.log('mouseLeave: ' + e.target.outerHTML)
    }
  }


  render() {

    const style = {
      background: "#fff",
      position: "absolute",
      top: "50%",
      left: "50%",
      transform: "translate(-50%,-50%)",
    }

    return (
      <section style={{"position":"relative"}} className="section-artboard section-bottom">
      <svg
          style={style}
          id="svg"
          version="1.1"
          width="500"
          height="400"
          xmlns="http://www.w3.org/2000/svg"
          fill="#fff"
          dangerouslySetInnerHTML={{__html: this.state.data.join('') }}
          onMouseDown={this.onMouseDown}
          onMouseUp={this.onMouseUp}
          onMouseMove={this.onMouseMove}
          onMouseLeave={this.onMouseLeave}
      >
      </svg>
      {/*<DraggableItem
          mainColor={this.props.mainColor}
          subColor={this.props.subColor}
          accentColor={this.props.accentColor}
      />*/}
      </section>
    );
  }
}

export default Artboard;
