import React,{useState,ureRef} from 'react';
//
//   DraggableItem.create('svg', editableItem);
//
//   * Drag Items
//   * Resize Items
//   * Modify colors of Items
//


/** 選んだ画像をドラッグして移動 **/

const DraggableItem = () => {
    const [x, setX] = useState(50); // 追記
    const [y, setY] = useState(50);

    const [isMouseDown, setIsMouseDown] = useState(false)
    const svgRef = useRef(null);

    // マウス押下時
    const onMouseDown = (e) => {
        setIsMouseDown(true);
    }

    // マウス押下解除時
    const onMouseUp = (e) => {
        setIsMouseDown(false);
    }

    // マウス移動時
    const onMouseMove = (e) => {
        // マウス押下中
        if (isMouseDown) {
            const mouseX = e.pageX;
            const mouseY = e.pageY;
        }
    }

    return (
      <rect
        width="100"
        height="100"
        ref={svgRef}
        x={x}
        y={y}
        fill="#900"
        stroke="#666"
        strokeWidth="5"
        onMouseDown={onMouseDown}
        onMouseUp={onMouseUp}
      />
    )
}

export default DraggableItem;
