import React from 'react';
import { Link } from 'react-router-dom'
import '../styles/menubar.scss';
import icon from '../images/logo.png';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChevronDown,faAdjust } from '@fortawesome/free-solid-svg-icons'
import ReactModal from 'react-modal';

import Document from "../document";

class MenuBar extends React.Component {

  // React Modal -- Start
  constructor () {
    super();
    this.state = {
      showModal: false
    };

    this.handleOpenModal = this.handleOpenModal.bind(this);
    this.handleCloseModal = this.handleCloseModal.bind(this);
  }

  handleOpenModal () {
    this.setState({ showModal: true });
  }

  handleCloseModal () {
    this.setState({ showModal: false });
  }
  // React Modal -- End

  render() {

    const styles = {
      overlay: {
        background: 'none',
      },
      content : {
        position: 'relative',
        width: '250px',
        top                   : '11.4%',
        left                  : '48%',
        right                 : 'auto',
        bottom                : 'auto',
        transform             : 'translate(-50%, -10%)',
        borderRadius: '0',
        border: 'solid 1px #F0F0F0',
        transition: 'ease all 1s',
      }
    };

    return (
      <section className="section-menubar">
        <Link to="/home" aria-label="Home"><img className="icon" src={icon} alt="Icon" /></Link>
        <div>
        <button className="modal-botton" onClick={this.handleOpenModal}>
          <p>{Document[0].title} <span><FontAwesomeIcon icon={faChevronDown} /></span></p>
        </button>
        <ReactModal style={styles} isOpen={this.state.showModal} contentLabel="Change Document Information">
          <form className="form-document" action="">
          <p>Change a document name.</p>
          <input type="text" value={Document[0].title}/>
          <button onClick={this.handleCloseModal}>Cancel</button>
          <input type="submit" value="Submit"/>
          </form>
        </ReactModal>
      </div>
        <div className="mode-change"><FontAwesomeIcon icon={faAdjust} /></div>
      </section>
    );

}
}

export default MenuBar;
