import React from 'react';
//import reactCSS from 'reactcss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSortAmountUp,faSortAmountDown } from '@fortawesome/free-solid-svg-icons'
import ColorPicker from "./toolspanel_ColorPicker";

class ToolsPanel extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      color: null,
    }
  }

  changeHex = (e) => {
    this.setState({color: e});
    this.props.changeHexOfSub(e);
  }

  render() {
    return (
      <section className="section-toolspanel section-bottom">
        <ul>
          <li><FontAwesomeIcon icon={faSortAmountUp} /></li>
          <li><FontAwesomeIcon icon={faSortAmountDown} /></li>
        </ul>

        <div className="color-scheme">
          <ColorPicker
             color={this.props.mainColor}
          />
          <ColorPicker color={this.props.subColor}/>
          <ColorPicker
            color={this.props.accentColor}
            onChange={()=>this.props.changeHex()}
            changeHexOfMain={this.fuun}
            changeHexOfSub={this.fuun}
            changeHexOfAccent={this.fuun}
          />
            {/*ref='CPSetting' />*/}
        </div>

      </section>
    );

  }
}


export default ToolsPanel;
