import React from 'react';
import { Redirect } from 'react-router-dom'

//import {
//  BrowserRouter as Router,
//  Switch,
//  Route,
//  Link
//} from "react-router-dom";
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';

import MenuBar from '../components/menubar.js';
import Navigation from '../components/navigation.js';
import '../styles/home.scss';

import icon from '../images/logo.png';
import Data from "../data";
import Template from "../template";
import ColorPicker from '../components/ColorPicker.js';

//import GallaryPanel from '../components/gallarypanel.js';
//import ToolsPanel from '../components/toolspanel.js';
//import Artboard from '../components/artboard.js';

class ColorSchemes extends React.Component {

  // Start - Get API Json

  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      users: []
    };
  }
  componentDidMount() {
    fetch("/api-of-user-json")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            users: result.users
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  // END - Get API Json

  render() {
    const { error, isLoaded, users } = this.state;
    if (error) {
      return <section className="section-home">Error: {error.message}<Redirect to="/login" /></section>;

    } else if (!isLoaded) {
      return <section className="section-home">Loading...</section>;
    } else {

      return (
        <div className="section-color-scheme">
          <div className="create-color-scheme">
            <h2>Create a new color scheme</h2>
            <input type="text" placeholder="Color scheme name"/>
            <ul>{/*li*/}</ul>
            <input type="submit" value="Create"/>
          </div>

          <div className="color-scheme-list">
            <div>
              {Data.map((data, i) => {
                return (
                  <div className="color-scheme-item" key={i}>
                    <p className="color-scheme-title">{data.CSTitle}</p>
                    <ul className="color-scheme">
                      <li>
                        {data.colors.map(c => (<span style={{'background': c}}></span>))}
                      </li>
                    </ul>
                  </div>
                )
              })}
              {users.map(item => (
                <li key={item.id}>
                  {item.color_schemes}
                </li>
              ))}
            </div>
          </div>
        </div>
      );
    }
  }
}

export default ColorSchemes;
