import React from 'react';
import { Redirect } from 'react-router-dom'

//import {
//  BrowserRouter as Router,
//  Switch,
//  Route,
//  Link
//} from "react-router-dom";

import Data from "../data";
//import ColorPicker from '../components/ColorPicker.js';

class ColorSchemes extends React.Component {

  // Start - Get API Json

  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      users: [],
    };
  }
  componentDidMount() {
    fetch("/api-of-user-json")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            users: result.users,
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  // END - Get API Json

  render() {
    const { error, isLoaded, users } = this.state;
    if (error) {
      return <section className="section-home">Error: {error.message}<Redirect to="/login" /></section>;

    } else if (!isLoaded) {
      return <section className="section-home">Loading...</section>;
    } else {

      const json = users.color_schemes;
      console.log(json);
      return (
        <div className="section-color-scheme">
          <div className="create-color-scheme">
            <h2>Create a new color scheme</h2>
            <input type="text" placeholder="Color scheme name"/>
            <ul>{/*li*/}</ul>
            <input type="submit" value="Create"/>
          </div>

          <div className="color-scheme-list">
            <div>
              {users.map(item => (
                <li key={item.id}>
                  <div className="color-scheme-item">
                  <ul className="color-scheme">
                  {JSON.parse(item.color_schemes).map(c => (
                    <li>
                      <p>{c.name}</p>
                      <span style={{'background': c.main}}></span>
                      <span style={{'background': c.sub}}></span>
                      <span style={{'background': c.accent}}></span>
                    </li>
                  ))}
                  </ul>
                  </div>
                  {/*.map(c => (
                    <li>
                      <p>{c.main} {c.sub}</p>
                      <p>{c.accent}</p>
                    </li>
                  ))*/}
                </li>
              ))}
            </div>
          </div>
        </div>
      );
    }
  }
}

export default ColorSchemes;
